"use strict";(self.webpackChunkchaavat=self.webpackChunkchaavat||[]).push([[252],{252:(l,s,t)=>{t.r(s),t.d(s,{title:()=>a,story:()=>c});const a="\u0928\u093e\u0908\u091f \u0915\u0945\u092e\u094d\u092a",c='\n<div class="fs-3">\n\u0906\u092e\u094d\u0939\u0940 \u0932\u094b\u0923\u093e\u0935\u0933\u094d\u092f\u093e\u0924 \u092a\u0941\u0923\u0947  \u092f\u0947\u0925\u0947 \u0939\u094b\u0924\u094b.\n\n\u0906\u092e\u094d\u0939\u0940 \u0930\u093e\u0924\u094d\u0930\u0940\u091a\u094d\u092f\u093e \u0915\u0945\u092e\u094d\u092a\u093f\u0902\u0917 \u0938\u0947\u0935\u0947\u0938\u093e\u0920\u0940 \u092c\u0941\u0915 \u0915\u0947\u0932\u0947 \u0939\u094b\u0924\u0947, \u0924\u0947 \u091c\u0935\u0933\u091a\u094d\u092f\u093e \u090f\u0915\u093e \u0924\u0932\u093e\u0935\u093e\u091c\u0935\u0933 \u0939\u094b\u0924\u0947, \u092e\u093e\u091d\u093e \u092e\u093f\u0924\u094d\u0930 \u0905\u0916\u093f\u0932\u0932\u093e \u092a\u094d\u0930\u093e\u0926\u0947\u0936\u093f\u0915 \u091a\u0945\u0928\u0947\u0932 \u0936\u094b\u0938\u093e\u0920\u0940 \u0936\u0949\u0930\u094d\u091f\u0932\u093f\u0938\u094d\u091f \u0915\u0930\u0923\u094d\u092f\u093e\u0924 \u0906\u0932\u0947 \u0939\u094b\u0924\u0947. \u0906\u092e\u094d\u0939\u0940 \u0926\u094b\u0918\u093e\u0902\u0928\u0940 \u090f\u092b\u0938\u0940 \u0915\u0949\u0932\u0947\u091c \u092a\u0941\u0923\u0947 \u092e\u0927\u094d\u092f\u0947 \u0906\u092e\u091a\u093e \u091a\u093f\u0924\u094d\u0930\u092a\u091f \u0905\u092d\u094d\u092f\u093e\u0938 \u0915\u0947\u0932\u093e \u0939\u094b\u0924\u093e, \u090f\u092b\u0938\u0940 \u0928\u0947  \u092c\u0949\u0932\u093f\u0935\u0942\u0921\u091a\u094d\u092f\u093e \u0935\u093f\u0915\u093e\u0938\u093e\u0924 \u092e\u094b\u0920\u0940 \u092d\u0942\u092e\u093f\u0915\u093e \u092c\u091c\u093e\u0935\u0932\u0940 \u0906\u0939\u0947.\n\n\u0906\u092e\u094d\u0939\u0940 \u0926\u094b\u0918\u0947 \u0905\u092d\u093f\u092f\u093e\u0902\u0924\u094d\u0930\u093f\u0915\u0940 \u092a\u093e\u0938\u0942\u0928 \u090f\u0915\u093e\u091a \u0935\u0930\u094d\u0917\u093e\u0924 \u0936\u093f\u0915\u0924 \u0939\u094b\u0924\u094b, \u0906\u092e\u094d\u0939\u0940 \u092e\u0939\u093e\u0935\u093f\u0926\u094d\u092f\u093e\u0932\u092f\u0940\u0928 \u0926\u093f\u0935\u0938\u093e\u0902\u092e\u0927\u094d\u092f\u0947 \u090f\u0915\u0924\u094d\u0930 \u092c\u0930\u0947\u091a \u0915\u093e\u0930\u094d\u092f\u0915\u094d\u0930\u092e \u0915\u0947\u0932\u0947 \u0906\u0923\u093f \u0915\u093e\u0930\u0923 \u0906\u092e\u091a\u0940 \u0906\u0935\u0921 \u0938\u093e\u0930\u0916\u0940 \u0939\u094b\u0924\u0940 \u0924\u094d\u092f\u093e\u092e\u0941\u0933\u0947 \u092e\u0948\u0924\u094d\u0930\u0940 \u0918\u091f\u094d\u091f \u091d\u093e\u0932\u0940 .\n\n\u0906\u092e\u094d\u0939\u0940 \u092a\u0941\u0923\u094d\u092f\u093e\u0924 \u092c\u0930\u093e\u091a \u0915\u093e\u0933 \u092c\u0945\u091a\u0932\u0930  \u092e\u094d\u0939\u0923\u0942\u0928 \u0930\u093e\u0939\u0924 \u0939\u094b\u0924\u094b, \u092a\u0930\u0902\u0924\u0941 \u0915\u093e\u0939\u0940 \u0926\u093f\u0935\u0938\u093e\u0902\u0928\u0940 \u0905\u0916\u093f\u0932\u0928\u0947 \u0924\u094d\u092f\u093e\u091a\u094d\u092f\u093e \u0906\u0908\u0932\u093e \u0907\u0915\u0921\u0947 \u0906\u0923\u0923\u094d\u092f\u093e\u0938\u093e\u0920\u0940 \u0932\u0917\u094d\u0928 \u0915\u0947\u0932\u0947, \u0905\u0916\u093f\u0932\u091a\u0947 \u0932\u0917\u094d\u0928 \u0939\u093e \u0924\u094d\u092f\u093e\u091a\u094d\u092f\u093e \u0906\u0908\u091a\u093e \u0928\u093f\u0930\u094d\u0923\u092f \u0939\u094b\u0924\u093e.\n\n\u0924\u094d\u092f\u093e\u091a\u094d\u092f\u093e \u0935\u0921\u093f\u0932\u093e\u0902\u091a\u093e \u0918\u091f\u0938\u094d\u092b\u094b\u091f \u091d\u093e\u0932\u093e \u0939\u094b\u0924\u093e, \u0924\u094d\u092f\u093e\u091a\u094d\u092f\u093e \u0906\u0908\u0932\u093e \u092b\u093f\u0932\u094d\u092e \u0907\u0902\u0921\u0938\u094d\u091f\u094d\u0930\u0940\u092e\u0927\u094d\u092f\u0947 \u092a\u094d\u0930\u092f\u0924\u094d\u0928 \u0915\u0930\u093e\u092f\u091a\u0947 \u0939\u094b\u0924\u0947, \u0906\u0923\u093f \u0924\u094d\u092f\u093e\u091a\u0947 \u0935\u0921\u0940\u0932 \u0924\u093f\u091a\u094d\u092f\u093e \u092c\u093e\u091c\u0942\u0928\u0947 \u0928\u0935\u094d\u0939\u0924\u0947, \u090f\u0915\u0926\u093e \u0924\u093f\u0928\u0947 \u090f\u0915 \u092e\u0941\u0932\u093e\u0916\u0924 \u0926\u093f\u0932\u0940  \u0939\u094b\u0924\u0940. \u0924\u094d\u092f\u093e\u0928\u0947 \u0905\u0938\u0947 \u092e\u093e\u0928\u0932\u0947 \u0915\u0940 \u0924\u093f\u0928\u0947 \u0924\u094d\u092f\u093e\u0902\u091a\u094d\u092f\u093e\u0936\u0940 \u0935\u093f\u0936\u094d\u0935\u093e\u0938\u0918\u093e\u0924 \u0915\u0947\u0932\u093e \u0906\u0923\u093f \u0924\u094d\u092f\u093e\u0902\u0928\u0940 \u0924\u093f\u0932\u093e \u0938\u094b\u0921\u0932\u0947.\n\n\u0924\u093f\u0928\u0947 \u0938\u094d\u0935\u0924\u0903 \u0905\u0916\u093f\u0932\u0932\u093e \u0935\u093e\u0922\u0935\u0932\u0947 \u200b\u200b\u0939\u094b\u0924\u0947. \u092e\u0940 \u090f\u0915\u091f\u093e \u0905\u0938\u0932\u094d\u092f\u093e\u0928\u0947 \u092e\u0940 \u0905\u0916\u093f\u0932 \u0918\u0930\u093e\u0936\u0947\u091c\u093e\u0930\u0940 \u092d\u093e\u0921\u094d\u092f\u093e\u0928\u0947 \u092b\u094d\u0932\u0945\u091f \u0918\u0947\u0924\u0932\u093e. \u092e\u0940 \u0911\u0921\u093f\u0936\u0928\u0938\u093e\u0920\u0940\u0939\u0940 \u092a\u094d\u0930\u092f\u0924\u094d\u0928 \u0915\u0930\u0924 \u0939\u094b\u0924\u094b. \u0905\u0916\u093f\u0932\u0932\u093e \u0905\u0917\u094b\u0926\u0930\u091a \u091a\u0947\u0915  \u092e\u093f\u0933\u093e\u0932\u093e \u0939\u094b\u0924\u093e , \u0915\u093e\u0930\u0923 \u091a\u0945\u0928\u0947\u0932 \u092e\u094b\u0920\u0947 \u0939\u094b\u0924\u0947 \u0906\u0923\u093f \u0924\u094b \u092e\u0939\u0924\u094d\u0935\u093e\u091a\u094d\u092f\u093e \u092d\u0942\u092e\u093f\u0915\u0947\u0924 \u0939\u094b\u0924\u093e, \u091a\u0945\u0928\u0947\u0932  \u092e\u093e \u0924\u094d\u092f\u093e\u0932\u093e \u0936\u0915\u094d\u092f \u0924\u093f\u0924\u0915\u0947 \u0924\u094d\u092f\u093e\u0932\u093e \u0938\u0902\u0924\u0941\u0937\u094d\u091f \u0915\u0930\u093e\u092f\u091a\u0947 \u0939\u094b\u0924\u0947.\n\n\u0924\u094b\u092a\u0930\u094d\u092f\u0902\u0924 \u0905\u0916\u093f\u0932\u0932\u093e \u090f\u0915 \u092e\u0942\u0932 \u0939\u094b\u0924\u0947. \u0905\u0916\u093f\u0932 \u0906\u0923\u093f \u0924\u094d\u092f\u093e\u091a\u094d\u092f\u093e \u092a\u0924\u094d\u0928\u0940\u0928\u0947 \u0939\u0940 \u092a\u093e\u0930\u094d\u091f\u0940 \u092a\u094d\u0932\u0945\u0928 \u0915\u0947\u0932\u0940  \u0939\u094b\u0924\u0940. \u0906\u092e\u094d\u0939\u0940 \u0938\u0915\u093e\u0933\u0940 \u0939\u093e\u092f\u0915\u093f\u0902\u0917\u0932\u093e \u0917\u0947\u0932\u094b, \u091c\u0935\u0933\u091a\u093e \u0928\u093f\u0938\u0930\u094d\u0917 \u0938\u0930\u094d\u0935\u093e\u0924 \u0936\u0941\u0926\u094d\u0927 \u0906\u0939\u0947. \u092e\u0940 \u0906\u0923\u093f \u0905\u0916\u093f\u0932 \u0925\u094b\u0921\u0947 \u092a\u0941\u0922\u0947 \u0917\u0947\u0932\u094b, \u0906\u092e\u094d\u0939\u0940 \u0911\u0921\u093f\u0936\u0928\u094d\u0938 \u0906\u0923\u093f \u092c\u093e\u0915\u0940 \u0917\u094b\u0937\u094d\u091f\u0940\u0902\u0935\u0930 \u091a\u0930\u094d\u091a\u093e \u0915\u0947\u0932\u0940, \u090f\u0915\u0926\u093e \u0924\u094d\u092f\u093e\u0928\u0947 \u0924\u094d\u092f\u093e\u091a\u0947 \u0935\u0947\u0933\u093e\u092a\u0924\u094d\u0930\u0915 \u0938\u0941\u0930\u0942 \u0915\u0947\u0932\u0947 \u0915\u0940 \u0924\u094b \u092e\u0932\u093e \u0924\u093f\u0925\u0947 \u0930\u0947\u092b\u0930 \u0915\u0930\u0923\u094d\u092f\u093e\u091a\u093e \u092a\u094d\u0930\u092f\u0924\u094d\u0928 \u0915\u0930\u0923\u093e\u0930 \u0939\u094b\u0924\u093e. \u0906\u092e\u094d\u0939\u0940 \u0924\u094d\u092f\u093e\u0935\u0930 \u091a\u0930\u094d\u091a\u093e \u0915\u0947\u0932\u0940 \u0906\u0923\u093f \u0928\u0902\u0924\u0930 \u0915\u093e\u0939\u0940 \u0938\u093e\u092e\u093e\u0928\u094d\u092f \u0917\u094b\u0937\u094d\u091f\u0940 \u0906\u092e\u094d\u0939\u093e\u0932\u093e \u092c\u094b\u0932\u093e\u092f\u0932\u093e \u0906\u0935\u0921\u0924\u093e\u0924. \u092a\u0930\u0924 \u0906\u0932\u094d\u092f\u093e\u0935\u0930 \u0906\u092e\u094d\u0939\u0940 \u092c\u094b\u091f\u093f\u0902\u0917\u0938\u093e\u0920\u0940 \u0917\u0947\u0932\u094b, \u0924\u093f\u0925\u0947 \u0906\u092f\u094b\u091c\u0915\u093e\u0902\u0928\u0940 \u0935\u094d\u092f\u0935\u0938\u094d\u0925\u093e \u0915\u0947\u0932\u0940 \u0939\u094b\u0924\u0940. \u0906\u092e\u094d\u0939\u0940 \u0915\u093e\u0939\u0940 \u0915\u093e\u0933 \u0905\u0916\u093f\u0932 \u091a\u094d\u092f\u093e \u092e\u0941\u0932\u093e\u0938\u094b\u092c\u0924 \u0916\u0947\u0933\u0932\u094b, \u0915\u093e\u0939\u0940 \u092b\u094b\u091f\u094b\u0936\u0942\u091f \u0915\u0947\u0932\u0947. \u0905\u0916\u093f\u0932 \u0906\u0923\u093f \u0924\u094d\u092f\u093e\u091a\u0940 \u092a\u0924\u094d\u0928\u0940 \u092a\u0930\u093f\u092a\u0942\u0930\u094d\u0923 \u091c\u094b\u0921\u092a\u0947 \u0906\u0939\u0947\u0924, \u0924\u0947 \u090f\u0915\u092e\u0947\u0915\u093e\u0902\u091a\u0940 \u0915\u093e\u0933\u091c\u0940 \u0918\u0947\u0924\u093e\u0924, \u0906\u0923\u093f \u0924\u094d\u092f\u093e\u0902\u0928\u0940 \u090f\u0915\u0924\u094d\u0930 \u0924\u094d\u092f\u093e\u091a\u094d\u092f\u093e \u0906\u0908\u091a\u0940 \u0916\u0942\u092a \u0915\u093e\u0933\u091c\u0940 \u0918\u0947\u0924\u0932\u0940 \u0906\u0923\u093f \u0905\u0916\u093f\u0932\u091a\u094d\u092f\u093e \u092e\u0941\u0932\u093e\u0928\u0947 \u0906\u0902\u091f\u0940\u091a\u093e \u0906\u0928\u0902\u0926 \u092a\u0930\u0924 \u0906\u0923\u0932\u093e \u0906\u0939\u0947.\n\n\u092e\u0940 \u0906\u0923\u093f \u0905\u0916\u093f\u0932 \u092e\u0917 \u091c\u0935\u0933\u091a\u094d\u092f\u093e \u0926\u0941\u0915\u093e\u0928\u093e\u0924\u0942\u0928 \u0915\u093e\u0939\u0940 \u092c\u093f\u0905\u0930 \u0906\u0923\u093e\u092f\u0932\u093e \u0917\u0947\u0932\u094b, \u091c\u0935\u0933\u091a \u092c\u093f\u0905\u0930 \u0936\u0949\u092a \u0936\u094b\u0927\u093e\u092f\u0932\u093e \u0925\u094b\u0921\u093e \u0935\u0947\u0933 \u0932\u093e\u0917\u0932\u093e. \u0906\u092e\u094d\u0939\u0940 \u092a\u0930\u0924 \u092f\u0947\u0908\u092a\u0930\u094d\u092f\u0902\u0924 \u0906\u0927\u0940\u091a \u0905\u0902\u0927\u093e\u0930 \u0939\u094b\u0924\u093e. \u0906\u092e\u094d\u0939\u0940 \u0906\u092f\u094b\u091c\u0915\u093e\u0902\u0928\u0940 \u0926\u093f\u0932\u0947\u0932\u094d\u092f\u093e \u092c\u093e\u0930\u092c\u0947\u0915\u094d\u092f\u0942\u092e\u0927\u094d\u092f\u0947 \u0906\u0917 \u0938\u0941\u0930\u0942 \u0915\u0947\u0932\u0940. \u0905\u0916\u093f\u0932\u091a\u093e \u092e\u0941\u0932\u0917\u093e \u0924\u094b\u092a\u0930\u094d\u092f\u0902\u0924 \u091d\u094b\u092a\u0932\u093e \u0939\u094b\u0924\u093e. \u092e\u0940 \u0905\u0916\u093f\u0932 \u0906\u0923\u093f \u0924\u094d\u092f\u093e\u091a\u094d\u092f\u093e \u092a\u0924\u094d\u0928\u0940\u0928\u0947 \u0915\u093e\u0939\u0940 \u091a\u0930\u094d\u091a\u093e \u0938\u0941\u0930\u0942 \u0915\u0947\u0932\u0940, \u0906\u0927\u0940\u091a\u0947 \u0906\u092f\u0941\u0937\u094d\u092f \u0915\u0938\u0947 \u0939\u094b\u0924\u0947 \u0906\u0923\u093f \u0938\u0930\u094d\u0935. \u0905\u0916\u093f\u0932 \u0906\u0923\u093f \u092e\u0940 \u092c\u094b\u0932\u0924 \u0905\u0938\u0924\u093e\u0928\u093e \u091c\u0935\u0933\u091c\u0935\u0933 4-4 \u092c\u093f\u0905\u0930 \u0938\u0902\u092a\u0935\u0932\u094d\u092f\u093e, \u0924\u094d\u092f\u093e\u091a\u0940 \u092c\u093e\u092f\u0915\u094b \u0906\u0923\u093f \u0915\u093e\u0915\u0942\u0902\u0928\u0940 \u092b\u0915\u094d\u0924 1-1 \u0918\u0947\u0924\u0932\u0947 \u0924\u094d\u092f\u093e\u092e\u0941\u0933\u0947 \u0906\u092e\u091a\u094d\u092f\u093e\u0915\u0921\u0947 \u0905\u0924\u093f\u0930\u093f\u0915\u094d\u0924 \u0939\u094b\u0924\u0947. \u0915\u093e\u0915\u0942 \u0906\u0923\u093f \u0905\u0916\u093f\u0932\u091a\u0940 \u092c\u093e\u092f\u0915\u094b \u0906\u092e\u091a\u094d\u092f\u093e \u092c\u094b\u0932\u0923\u094d\u092f\u093e\u091a\u093e \u0906\u0928\u0902\u0926 \u0918\u0947\u0924 \u0939\u094b\u0924\u0947, \u092e\u094d\u0939\u0923\u0942\u0928 \u0924\u094d\u092f\u093e\u0902\u0928\u0940 \u0935\u094d\u092f\u0924\u094d\u092f\u092f \u0906\u0923\u0932\u093e \u0928\u093e\u0939\u0940, \u0924\u0941\u092e\u094d\u0939\u093e\u0932\u093e \u092e\u093e\u0939\u093f\u0924\u0940 \u0906\u0939\u0947 \u0915\u0940 \u0926\u094b\u0928 \u092c\u093e\u091f\u0932\u094d\u092f\u093e\u0902\u0928\u0902\u0924\u0930 \u092e\u093e\u0923\u0942\u0938 \u0915\u093f\u0924\u0940 \u092c\u0921\u092c\u0921\u0924\u094b ? \u0906\u0924\u093e \u092a\u0939\u093e\u091f\u0947 2 \u0935\u093e\u091c\u0932\u0947 \u0939\u094b\u0924\u0947, \u0906\u0923\u093f \u0916\u0942\u092a \u0925\u0902\u0921\u0940 \u0939\u094b\u0924\u0940, \u092e\u094d\u0939\u0923\u0942\u0928 \u0906\u092e\u094d\u0939\u0940 \u0906\u0924\u093e  \u0924\u0902\u092c\u0942\u0924 \u091c\u093e\u0923\u094d\u092f\u093e\u091a\u093e \u0928\u093f\u0930\u094d\u0923\u092f \u0918\u0947\u0924\u0932\u093e, \u0906\u092e\u094d\u0939\u0940 \u0936\u0947\u0935\u091f\u091a\u094d\u092f\u093e \u092e\u093f\u0928\u093f\u091f\u093e\u0902\u091a\u094d\u092f\u093e \u092c\u0941\u0915\u093f\u0902\u0917\u092e\u0941\u0933\u0947 \u0905\u0928\u0941\u092a\u0932\u092c\u094d\u0927\u0924\u0947\u092e\u0941\u0933\u0947 \u0926\u094b\u0928 \u0924\u0902\u092c\u0942 \u092c\u0941\u0915 \u0915\u0947\u0932\u0947 \u0939\u094b\u0924\u0947. \u0915\u093e\u0915\u0942 \u092e\u094d\u0939\u0923\u093e\u0932\u094d\u092f\u093e \u0905\u0916\u093f\u0932 \u0906\u0923\u093f \u0905\u0928\u0940\u0938 \u0928\u0947 \u092f\u093e \u092e\u0947\u091c\u0935\u093e\u0928\u0940\u091a\u0940 \u0935\u094d\u092f\u0935\u0938\u094d\u0925\u093e \u0915\u0930\u0923\u094d\u092f\u093e\u0938\u093e\u0920\u0940 \u0916\u0942\u092a \u092e\u0947\u0939\u0928\u0924 \u0918\u0947\u0924\u0932\u0940 \u0939\u094b\u0924\u0940, \u0924\u0941\u092e\u094d\u0939\u0940 \u0932\u094b\u0915 \u092e\u091c\u093e \u0915\u0930\u093e, \u0924\u094d\u092f\u093e\u0902\u0928\u0940  \u0905\u0916\u093f\u0932 \u091a\u094d\u092f\u093e \u092e\u0941\u0932\u093e\u0932\u093e \u0938\u094b\u092c\u0924  \u0918\u0947\u0924\u0932\u0947, \u0906\u0923\u093f \u092e\u0932\u093e \u092a\u0941\u0922\u0947 \u091a\u093e\u0932\u093e\u092f\u0932\u093e \u0938\u093e\u0902\u0917\u093f\u0932\u0924, \u092e\u0932\u093e \u0905\u0915\u094d\u0937\u0930\u0936\u0903 \u091a\u093e\u0932\u0923\u0947 \u0938\u0941\u0926\u094d\u0927\u093e \u0936\u0915\u094d\u092f \u0928\u0935\u094d\u0939\u0924\u0947. \u092e\u0940 \u0935\u093f\u091a\u093e\u0930 \u0915\u0947\u0932\u093e \u0915\u0940 \u092f\u093e \u092e\u0926\u094d\u092f\u0927\u0941\u0902\u0926 \u0905\u0935\u0938\u094d\u0925\u0947\u0924 \u0905\u0916\u093f\u0932 \u0915\u093e\u092f \u092e\u091c\u093e \u0915\u0930\u0923\u093e\u0930 \u0906\u0939\u0947?, \u092e\u0940 \u0915\u0938\u093e \u0924\u0930\u0940 \u0924\u0902\u092c\u0942\u0924 \u092a\u094b\u0939\u094b\u091a\u0932\u094b.\n\n\u0915\u093e\u0915\u0942 \u0906\u0923\u093f \u0905\u0928\u093f\u0938 \u0906\u092e\u091a\u094d\u092f\u093e\u0915\u0921\u0947 \u092a\u093e\u0939\u0942\u0928 \u0939\u0938\u0924 \u0939\u094b\u0924\u0947. \u0915\u093e\u0915\u0942 \u0905\u0916\u093f\u0932 \u091a\u094d\u092f\u093e \u092e\u0941\u0932\u093e\u0932\u093e \u0938\u094b\u092c\u0924 \u0918\u0947\u090a\u0928 \u0924\u0902\u092c\u0942\u0924 \u0906\u0932\u094d\u092f\u093e. \u092e\u0940 \u092a\u0942\u0930\u094d\u0923\u092a\u0923\u0947 \u0927\u0941\u0902\u0926 \u0939\u094b\u0924\u094b, \u0906\u0923\u093f \u091d\u094b\u092a\u093e\u092f\u0932\u093e \u0917\u0947\u0932\u094b, \u092e\u0940 \u0917\u093e\u0926\u0940\u0932\u093e \u0938\u094d\u092a\u0930\u094d\u0936 \u0915\u0930\u0924\u093e\u091a \u091d\u094b\u092a\u0932\u094b, \u0917\u093e\u0921\u0940 \u091a\u093e\u0902\u0917\u0932\u0940 \u0939\u094b\u0924\u0940 .\n\n\u0915\u093e\u0939\u0940 \u0935\u0947\u0933\u093e\u0928\u0947 \u092e\u0932\u093e \u092e\u093e\u091d\u094d\u092f\u093e \u0921\u093f\u0915 \u091c\u0935\u0933 \u0915\u093e\u0939\u0940 \u0939\u093e\u0932\u091a\u093e\u0932 \u0926\u093f\u0938\u0932\u0940, \u092e\u0940 \u0925\u094b\u0921\u0940 \u0909\u0920\u0932\u094b, \u0939\u093e\u0932\u091a\u093e\u0932 \u0925\u093e\u0902\u092c\u0932\u0940, \u092a\u0923 \u092e\u0932\u093e \u092e\u093e\u091d\u094d\u092f\u093e \u092a\u0945\u0928\u094d\u091f \u092e\u0927\u094d\u092f\u0947 \u0915\u093e\u0939\u0940 \u0925\u0902\u0921 \u0939\u0935\u093e \u0905\u0938\u0932\u094d\u092f\u093e\u0938\u093e\u0930\u0916\u0947 \u0935\u093e\u091f\u0932\u0947.... \n\u0915\u093e\u092f? \u092e\u093e\u091d\u0947 \u091a\u0921\u094d\u0921\u0940 \u0915\u093e\u0922\u0932\u0947\u0932\u0940 \u0939\u094b\u0924\u0940, \u092e\u0932\u093e \u0925\u094b\u0921\u0947 \u0938\u092e\u091c\u0932\u0947 \u0906\u0923\u093f \u0915\u093e\u0939\u0940\u0939\u0940 \u091d\u093e\u0932\u0947 \u0928\u093e\u0939\u0940 \u092e\u094d\u0939\u0923\u0942\u0928 \u092e\u0940 \u092a\u0941\u0928\u094d\u0939\u093e \u091d\u094b\u092a\u0932\u094b.\n\n\u0906\u0924\u093e \u0924\u094b \u0915\u094d\u0937\u0923 \u092a\u0941\u0928\u094d\u0939\u093e \u0938\u0941\u0930\u0941 \u091d\u093e\u0932\u093e, \u0924\u0940 \u092e\u093e\u091d\u094d\u092f\u093e \u0921\u093f\u0915\u091a\u0940 \u0915\u093e\u0924\u0921\u0940 \u0924\u093f\u091a\u094d\u092f\u093e \u0939\u093e\u0924\u093e\u0928\u0947 \u0935\u0930 \u0916\u093e\u0932\u0940 \u0915\u0930\u0924 \u0939\u094b\u0924\u0940. \u092e\u093e\u091d\u093e \u0921\u093f\u0915 \u0906\u0927\u0940\u091a \u0909\u092d\u093e \u0939\u094b\u0924\u093e, \u092e\u0940 \u0909\u0920\u0923\u094d\u092f\u093e\u092a\u0942\u0930\u094d\u0935\u0940\u091a. \u092e\u0940 \u0906\u0924\u093e \u092e\u0947\u0932\u094d\u092f\u093e\u0938\u093e\u0930\u0916\u093e \u0916\u093e\u0932\u0940 \u092a\u0921\u0932\u094b, \u0924\u093f\u0928\u0947 \u0906\u0924\u093e \u0939\u093e\u0924 \u0935\u0947\u0917\u093e\u0928\u0947 \u0939\u0932\u0935\u093e\u092f\u0932\u093e \u0938\u0941\u0930\u0941\u0935\u093e\u0924 \u0915\u0947\u0932\u0940. \u092e\u0932\u093e \u091c\u093e\u0917\u0947 \u0935\u094d\u0939\u093e\u092f\u091a\u0947 \u0928\u0935\u094d\u0939\u0924\u0947, \u0915\u093e\u0930\u0923 \u092e\u0932\u093e \u092d\u0940\u0924\u0940 \u0935\u093e\u091f\u0924 \u0939\u094b\u0924\u0940 \u0915\u0940 \u091c\u0930 \u0915\u093e\u0939\u0940 \u091a\u0941\u0915\u0940\u091a\u0947 \u091d\u093e\u0932\u0947 \u0924\u0930 \u092e\u0940 \u092e\u093e\u091d\u093e \u092e\u093f\u0924\u094d\u0930 \u0917\u092e\u093e\u0935\u0942\u0928 \u092c\u0938\u0947\u0932 . \u092e\u0932\u093e \u0905\u092a\u0930\u093e\u0927\u0940 \u0935\u093e\u091f\u0924 \u0939\u094b\u0924\u0947, \u092a\u0923 \u0906\u0928\u0902\u0926 \u092e\u093e\u091d\u094d\u092f\u093e \u092d\u093e\u0935\u0928\u093e\u0902\u0935\u0930 \u0935\u0930\u091a\u0922 \u0939\u094b\u0924\u093e. \u092e\u0940 \u0915\u093e\u0939\u0940 \u0939\u093e\u0932\u091a\u093e\u0932\u0940 \u0926\u093e\u0916\u0935\u0932\u094d\u092f\u093e \u0906\u0923\u093f \u0926\u093e\u0916\u0935\u0932\u0947 \u0915\u0940 \u092e\u0940 \u092a\u0942\u0930\u094d\u0923 \u0928\u0936\u0947\u0924 \u0906\u0939\u0947 , \u0916\u0930\u0902 \u0924\u0930, \u092e\u0940 \u0939\u094b\u0924\u094b \u092a\u0923, \u0932\u0948\u0902\u0917\u093f\u0915 \u0906\u0928\u0902\u0926 \u0906\u0923\u093f \u0905\u0932\u094d\u0915\u094b\u0939\u094b\u0932 \u0935\u0947\u0917\u0933\u094d\u092f\u093e \u0917\u094b\u0937\u094d\u091f\u0940 \u0906\u0939\u0947\u0924.\n\n\u0924\u093f\u0932\u093e \u0906\u0924\u093e \u0938\u092e\u091c\u0932\u0947 \u0939\u094b\u0924\u0947  \u0915\u0940 \u092e\u0940 \u091c\u093e\u0938\u094d\u0924\u091a \u0928\u0936\u0947\u0924  \u0906\u0939\u0947 \u0906\u0923\u093f \u0924\u0940 \u0924\u093f\u0932\u093e \u092a\u093e\u0939\u093f\u091c\u0947 \u0924\u0947 \u0915\u0930\u0942 \u0936\u0915\u0924\u0947. \u0924\u093f\u0928\u0947 \u0906\u0924\u093e \u092e\u093e\u091d\u094d\u092f\u093e \u092a\u093e\u092f\u093e\u0924\u0942\u0928 \u092e\u093e\u091d\u0940 \u092a\u0901\u091f \u0915\u093e\u0922\u0942\u0928 \u091f\u093e\u0915\u0932\u0940, \u0924\u093f\u0928\u0947 \u0939\u0933\u0942\u0939\u0933\u0942 \u092e\u093e\u091d\u094d\u092f\u093e \u092e\u093e\u0902\u0921\u0940\u0935\u0930 \u0939\u0932\u0915\u0940 \u092e\u0938\u093e\u091c \u0938\u0941\u0930\u0942 \u0915\u0947\u0932\u0940, \u0924\u093f\u0928\u0947 \u0906\u0924\u093e \u092e\u093e\u091d\u0947 \u092c\u0949\u0932\u094d\u0938 \u0918\u0947\u0924\u0932\u0947. \u092e\u0932\u093e \u0906\u0924\u093e \u092e\u093e\u091d\u094d\u092f\u093e \u0921\u093f\u0915 \u0935\u0930 \u0909\u092c\u0926\u093e\u0930 \u0935\u093e\u091f\u0932\u0947, \u0926\u0947\u0935\u093e, \u0924\u0940 \u0906\u0924\u093e \u092e\u093e\u091d\u093e \u0921\u093f\u0915 \u091a\u094b\u0916\u0924 \u0939\u094b\u0924\u0940. \u0924\u0940 \u090f\u0915\u094d\u0938\u094d\u092a\u0930\u094d\u091f \u0939\u094b\u0924\u0940, \u0924\u0940 \u091a\u094b\u0916\u0924\u093e\u0928\u093e \u0915\u0920\u094b\u0930 \u0938\u094d\u091f\u094d\u0930\u094b\u0915 \u0918\u0947\u0924 \u0939\u094b\u0924\u0940, \u0906\u0923\u093f \u0915\u0927\u0940\u0915\u0927\u0940 \u0924\u0940 \u0939\u0933\u0942 \u0939\u0933\u0942 \u0915\u0930\u0924 \u0939\u094b\u0924\u0940. \u0924\u0940 \u092e\u093e\u091d\u0947 \u0917\u094b\u0933\u0947 \u0924\u093f\u091a\u094d\u092f\u093e \u092e\u0927\u094d\u092f\u0947 \u0924\u094b\u0902\u0921\u093e\u0924 \u0918\u0947\u0924 \u0939\u094b\u0924\u0940. \u0924\u093f\u0932\u093e \u0906\u0924\u093e \u0938\u092e\u091c\u0932\u0947 \u0939\u094b\u0924\u0947 \u0915\u0940 \u092e\u0940 \u092a\u0942\u0930\u094d\u0923\u092a\u0923\u0947 \u0926\u093e\u0930\u0942\u091a\u094d\u092f\u093e \u0906\u0939\u093e\u0930\u0940 \u0917\u0947\u0932\u094b \u0906\u0939\u0947, \u0924\u093f\u0928\u0947 \u0906\u0924\u093e \u0939\u0933\u0942\u0939\u0933\u0942 \u092e\u093e\u091d\u0947 \u092a\u093e\u092f \u092a\u0938\u0930\u0935\u0932\u0947 \u0906\u0923\u093f \u0916\u093e\u0932\u0940 \u0917\u0947\u0932\u0940, \u0924\u093f\u0932\u093e \u0906\u0924\u093e \u092e\u093e\u091d\u094d\u092f\u093e \u0916\u093e\u0932\u091a\u094d\u092f\u093e \u092d\u093e\u0917\u093e\u0924 \u092a\u0942\u0930\u094d\u0923 \u092a\u094d\u0930\u0935\u0947\u0936 \u0939\u094b\u0924\u093e. \u092e\u093e\u091d\u094d\u092f\u093e \u091a\u0947\u0902\u0921\u0942\u0902\u0935\u0930 \u0915\u093e\u0939\u0940\u0939\u0940 \u091f\u092a\u0915\u0924 \u0928\u0935\u094d\u0939\u0924\u0947  \u0924\u0940 \u0938\u0930\u094d\u0935 \u0915\u093e\u0939\u0940 \u092a\u0940\u0924 \u0939\u094b\u0924\u0940, \u0924\u0940 \u092e\u093e\u091d\u094d\u092f\u093e \u0921\u093f\u0915\u0939\u0947\u0921\u0935\u0930 \u0924\u093f\u091a\u094d\u092f\u093e \u091c\u0940\u092d\u093e\u0928\u0947 \u0935\u0930\u094d\u0924\u0941\u0933\u0947 \u092c\u0928\u0935\u0924 \u0939\u094b\u0924\u0940, \u0913\u0939 ... \u0916\u0942\u092a \u091b\u093e\u0928 \u0935\u093e\u091f\u0924 \u0939\u094b\u0924\u0947 . \u0924\u0940 \u092e\u0932\u093e \u091a\u094b\u0916\u0924 \u0905\u0938\u0924\u093e\u0928\u093e \u0938\u094d\u091f\u094d\u0930\u094b\u0915 \u092e\u093e\u0930\u0924 \u0939\u094b\u0924\u0940, \u092e\u0940 \u0915\u0927\u0940\u0939\u0940 \u0938\u094d\u0916\u0932\u0928 \u0915\u0930\u0942 \u0936\u0915\u0924\u094b \u0905\u0938\u0947 \u0935\u093e\u091f \u0939\u094b\u0924\u0947 \u0906\u0923\u093f \u0906\u0924\u093e \u092e\u0940 \u0939\u0947 \u0926\u093e\u0916\u0935\u0942 \u0936\u0915\u0924 \u0928\u093e\u0939\u0940 \u0915\u0940 \u092e\u0940 \u092f\u0947\u090a \u0936\u0915\u0924\u094b.\n\u092a\u0930\u0902\u0924\u0941 \u091c\u0930 \u0924\u0941\u092e\u094d\u0939\u093e\u0932\u093e \u092e\u093e\u0939\u093f\u0924 \u0905\u0938\u0947\u0932 \u0924\u0930 \u0938\u094d\u0916\u0932\u0928 \u0939\u094b\u0923\u094d\u092f\u093e\u092a\u0942\u0930\u094d\u0935\u0940 \u092a\u094d\u0930\u093f\u0915\u092e \u0925\u094b\u0921\u093e \u0935\u093e\u0922\u0924\u094b, \u0906\u0923\u093f \u092e\u0932\u093e \u0935\u093e\u091f\u0924\u0947 \u0915\u0940 \u0924\u093f\u0932\u093e \u092e\u093e\u0939\u093f\u0924 \u0939\u094b\u0924\u0947 ,\u0924\u0940 \u090f\u0915\u094d\u0938\u094d\u092a\u0930\u094d\u091f  \u0939\u094b\u0924\u0940, \u0924\u093f\u0928\u0947  \u0938\u094d\u092a\u0940\u0921 \u0932\u0917\u0947\u091a \u0915\u092e\u0940 \u0915\u0947\u0932\u093e , \u0924\u0940 \u092e\u0932\u093e \u092e\u0947\u0902\u0926\u0942-\u0928\u093f\u092f\u0902\u0924\u094d\u0930\u093f\u0924 \u092f\u0902\u0924\u094d\u0930\u093e\u092a\u094d\u0930\u092e\u093e\u0923\u0947 \u0906\u0928\u0902\u0926 \u0926\u0947\u0924 \u0939\u094b\u0924\u0940. \u0924\u093f\u0928\u0947 \u092e\u0932\u093e \u0905\u0915\u094d\u0937\u0930\u0936\u0903 \u090f\u0915 \u0924\u093e\u0938 \u091a\u094b\u0916\u0932\u0947. \u092e\u0932\u093e \u0906\u0924\u093e \u0938\u094d\u0935\u0924\u0903\u0932\u093e \u0938\u094d\u0916\u0932\u0928 \u0935\u094d\u0939\u093e\u092f\u091a\u0947 \u0939\u094b\u0924\u0947, \u092e\u0940 \u0906\u0928\u0902\u0926\u093e\u091a\u094d\u092f\u093e \u090f\u0935\u094d\u0939\u0930\u0947\u0938\u094d\u091f\u0935\u0930 \u0939\u094b\u0924\u094b. \u092e\u0940 \u0938\u0941\u091f\u0932\u094b , \u0924\u0940 \u0924\u094d\u092f\u093e \u0915\u094d\u0937\u0923\u093e\u091a\u0940 \u0935\u093e\u091f \u092a\u093e\u0939\u0924 \u0939\u094b\u0924\u0940, \u0924\u093f\u0928\u0947 \u0938\u094d\u091f\u094d\u0930\u094b\u0915 \u0935\u093e\u0922\u0935\u0932\u0947, \u0924\u093f\u091a\u094d\u092f\u093e \u0924\u094b\u0902\u0921\u093e\u0924 \u092f\u0947\u0924\u093e\u0928\u093e \u0936\u0930\u0940\u0930 \u0906\u0930\u093e\u092e\u0936\u0940\u0930 \u0920\u0947\u0935\u0923\u0947 \u0916\u0942\u092a \u0915\u0920\u0940\u0923 \u0939\u094b\u0924\u0947,\n\n\u092e\u0940 \u0939\u0932\u0932\u094b \u092a\u0923 \u0905\u0928\u093f\u092f\u0902\u0924\u094d\u0930\u093f\u0924 \u0939\u093e\u0932\u091a\u093e\u0932\u0940 \u0915\u0947\u0932\u094d\u092f\u093e \u091c\u0938\u0947 \u0915\u093e\u0939\u0940  \u092e\u0940 \u092a\u0942\u0930\u094d\u0923 \u0928\u0936\u0947\u0924 \u0906\u0939\u0947. \u0924\u093f\u0928\u0947 \u092e\u093e\u091d\u093e \u092a\u0942\u0930\u094d\u0923 \u0921\u093f\u0915 \u0906\u0923\u093f \u0924\u094d\u092f\u093e\u091a\u0947 \u0915\u092e \u0918\u0947\u0924\u0932\u0947. \u0924\u0940 \u0930\u093f\u0932\u0945\u0915\u094d\u0938 \u0939\u094b\u0923\u094d\u092f\u093e\u091a\u094d\u092f\u093e \u092e\u0942\u0921\u092e\u0927\u094d\u092f\u0947 \u0928\u0935\u094d\u0939\u0924\u0940, \u0924\u093f\u0928\u0947 \u0905\u091c\u0942\u0928\u0939\u0940 \u0921\u093f\u0915 \u0924\u094b\u0902\u0921\u093e\u0924 \u0920\u0947\u0935\u0932\u093e \u0939\u094b\u0924\u093e , \u092e\u093e\u091d\u093e \u0921\u093f\u0915  \u0938\u094d\u0916\u0932\u0928\u093e\u0928\u0902\u0924\u0930 \u0925\u094b\u0921\u093e\u0938\u093e \u0938\u0902\u0915\u0941\u091a\u093f\u0924 \u091d\u093e\u0932\u093e  \u0939\u094b\u0924\u093e  \u0906\u0923\u093f \u0924\u093f\u0932\u093e \u0906\u0924\u093e \u091a\u094b\u0916\u0923\u0947 \u0938\u094b\u092a\u0947 \u0939\u094b\u0924\u0947, \u0924\u0940 \u0935\u094d\u0939\u0945\u0915\u094d\u092f\u0942\u092e \u092e\u0936\u0940\u0928\u092a\u094d\u0930\u092e\u093e\u0923\u0947 \u0924\u0940 \u091a\u094b\u0916\u0924 \u0939\u094b\u0924\u0940.\n\n\u0906\u0924\u093e \u0921\u093f\u0915  \u092a\u0941\u0928\u094d\u0939\u093e \u0924\u093e\u0920 \u091d\u093e\u0932\u093e , \u0924\u0940 \u090f\u0915\u093e \u0939\u093e\u0924\u093e\u0928\u0947 \u0938\u094d\u091f\u094d\u0930\u094b\u0915 \u0926\u0947\u0924 \u0930\u093e\u0939\u093f\u0932\u0940, \u092e\u0940 \u0924\u093f\u0932\u093e \u0925\u094b\u0921\u094d\u092f\u093e \u0909\u0918\u0921\u094d\u092f\u093e \u0921\u094b\u0933\u094d\u092f\u093e\u0902\u0928\u0940 \u092a\u093e\u0939\u0923\u094d\u092f\u093e\u091a\u093e \u092a\u094d\u0930\u092f\u0924\u094d\u0928 \u0915\u0930\u0924 \u0939\u094b\u0924\u094b, \u092e\u093e\u091d\u093e \u090f\u0915 \u0939\u093e\u0924 \u0935\u0930 \u0939\u094b\u0924\u093e, \u0921\u094b\u0933\u0947 \u091c\u0947\u0923\u0947\u0915\u0930\u0942\u0928 \u0924\u0940 \u092e\u0932\u093e \u0924\u093f\u091a\u094d\u092f\u093e\u0915\u0921\u0947 \u092c\u0918\u0924 \u0928\u093e\u0939\u0940 \u0905\u0938\u0947 \u0935\u093e\u091f\u0947\u0932 , \u092a\u0923 \u0924\u093f\u0925\u0947 \u0916\u0942\u092a \u0905\u0902\u0927\u093e\u0930 \u0939\u094b\u0924\u093e, \u092e\u0932\u093e \u0928\u0940\u091f \u0926\u093f\u0938\u0924 \u0928\u0935\u094d\u0939\u0924\u0947 . \u092e\u0940 \u0924\u093f\u091a\u094d\u092f\u093e \u0939\u093e\u0932\u091a\u093e\u0932\u0940 \u092a\u093e\u0939\u0923\u094d\u092f\u093e\u0938 \u0938\u0915\u094d\u0937\u092e \u0939\u094b\u0924\u094b.\n\n\u0924\u0940 \u0906\u0924\u093e \u0924\u093f\u091a\u0940 \u092a\u0901\u091f \u0915\u093e\u0922\u0924 \u0939\u094b\u0924\u0940, \u092e\u093e\u091d\u093e \u0921\u093f\u0915 \u0906\u0924\u093e \u0906\u0923\u0916\u0940 \u0924\u093e\u0920 \u0909\u092d\u093e \u0939\u094b\u0924 \u0939\u094b\u0924\u093e, \u092a\u0941\u0922\u091a\u093e \u092f\u0947\u0923\u093e\u0930\u093e \u0915\u093e\u0930\u094d\u092f\u0915\u094d\u0930\u092e \u092a\u093e\u0939\u0942\u0928. \u0924\u0940 \u092e\u093e\u091d\u094d\u092f\u093e\u0935\u0930 \u0906\u0932\u0940 \u0906\u0923\u093f \u092e\u093e\u091d\u093e \u0921\u093f\u0915 \u0924\u093f\u091a\u094d\u092f\u093e \u092e\u0927\u094d\u092f\u0947 \u0918\u093e\u0924\u0932\u093e, \u092e\u0940 \u0925\u094b\u0921\u093e \u0915\u093e\u0933\u091c\u0940\u0924 \u0939\u094b\u0924\u094b, \u092a\u0923 \u092e\u0940 \u0926\u093e\u0930\u0942 \u092a\u094d\u092f\u093e\u092f\u0932\u094d\u092f\u093e\u091a\u093e \u0906\u0935 \u0906\u0923\u0924 \u0939\u094b\u0924\u094b \u0906\u0923\u093f \u092e\u093e\u091d\u094d\u092f\u093e\u0915\u0921\u0947 \u0924\u0947 \u0939\u094b\u090a \u0926\u0947\u0923\u094d\u092f\u093e\u0936\u093f\u0935\u093e\u092f \u0926\u0941\u0938\u0930\u093e \u092e\u093e\u0930\u094d\u0917 \u0928\u0935\u094d\u0939\u0924\u093e.\n\n\u0924\u093f\u091a\u0940 \u092a\u0941\u091a\u094d\u091a\u0940 \u0918\u091f\u094d\u091f \u092a\u0923 \u0913\u0932\u0940 \u0939\u094b\u0924\u0940, \u0915\u0926\u093e\u091a\u093f\u0924 \u0924\u0940 \u092c\u0930\u094d\u092f\u093e\u091a \u0915\u093e\u0933\u093e\u0938\u093e\u0920\u0940 \u0928 \u0935\u093e\u092a\u0930\u0932\u0947\u0932\u0940 \u0939\u094b\u0924\u0940, \u0924\u093f\u0928\u0947 \u0906\u0924\u093e \u0939\u093e\u0932\u091a\u093e\u0932 \u0938\u0941\u0930\u0942 \u0915\u0947\u0932\u0940. \u092e\u0940 \u0939\u0933\u0942 \u0939\u0933\u0942 \u092e\u093e\u091d\u0940 \u0915\u0902\u092c\u0930 \u0935\u0930 \u0939\u0932\u0935\u0924 \u0939\u094b\u0924\u094b, \u092a\u0923 \u0916\u0942\u092a \u0939\u0933\u0942\u0939\u0933\u0942 \u091c\u0947\u0923\u0947\u0915\u0930\u0942\u0928 \u0924\u093f\u091a\u094d\u092f\u093e \u0932\u0915\u094d\u0937\u093e\u0924 \u092f\u0947\u090a \u0928\u092f\u0947, \u091c\u0947\u0923\u0947\u0915\u0930\u0942\u0928 \u0916\u094b\u0932\u0935\u0930 \u091c\u093e\u0935\u0947. \u0924\u0940 \u0916\u0942\u092a \u0938\u094d\u091f\u094d\u0930\u0949\u0902\u0917 \u0939\u094b\u0924\u0940  \u0906\u0923\u093f \u0924\u0940 \u0924\u093f\u091a\u0940 \u092a\u0941\u091a\u094d\u091a\u0940 \u092e\u093e\u091d\u094d\u092f\u093e \u0935\u0930 \u0935\u0947\u0917\u093e\u0928\u0947 \u0939\u0932\u0935\u0924 \u0939\u094b\u0924\u0940.\n\n\u0924\u093f\u0928\u0947 \u0939\u0947 \u0916\u0942\u092a \u0935\u0947\u0933 \u0915\u0947\u0932\u0947 \u0915\u093e\u0930\u0923 \u090f\u0915\u0924\u0930\u094d\u092b\u0940 \u0938\u0947\u0915\u094d\u0938 \u0939\u094b\u0924 \u0939\u094b\u0924\u0947 . \u092e\u0940 \u092c\u0930\u093e\u091a \u0915\u093e\u0933 \u091f\u093f\u0915\u0932\u094b, \u0924\u093f\u0928\u0947 \u0928\u0902\u0924\u0930 \u092e\u093e\u091d\u093e \u0921\u093f\u0915 \u092a\u0941\u0928\u094d\u0939\u093e \u0924\u093f\u091a\u094d\u092f\u093e \u0924\u094b\u0902\u0921\u093e\u0924 \u0918\u0947\u0924\u0932\u093e, \u092e\u0917 \u092a\u0941\u0928\u094d\u0939\u093e \u092e\u093e\u091d\u094d\u092f\u093e \u0921\u093f\u0915 \u0935\u0930 \u092c\u0938\u0932\u0940, \u0924\u0940  \u0906\u0924\u093e \u092e\u093e\u091d\u094d\u092f\u093e \u092c\u0949\u0932\u0935\u0930 \u0939\u093e\u0924 \u092b\u093f\u0930\u0935\u0924 \u0939\u094b\u0924\u0940, \u0924\u093f\u0928\u0947 \u0906\u0924\u093e \u0938\u094d\u092a\u0940\u0921 \u0935\u093e\u0922\u0935\u0932\u093e, \u0924\u093f\u091a\u0940 \u0935\u0947\u0917\u0935\u093e\u0928 \u0939\u093e\u0932\u091a\u093e\u0932 \u0905\u0938\u0932\u094d\u092f\u093e\u092e\u0941\u0933\u0947  \u0924\u0933\u093e\u092a\u093e\u0938\u0942\u0928 \u0905\u0927\u093f\u0915 \u0927\u0915\u094d\u0915\u093e \u0926\u0947\u0923\u094d\u092f\u093e\u091a\u0940 \u0938\u0902\u0927\u0940 \u092e\u093f\u0933\u0924 \u0939\u094b\u0924\u0940, \u092e\u0940 \u0924\u0940\u0935\u094d\u0930 \u091d\u093e\u0932\u094b \u0906\u0923\u093f \u092e\u0940 \u0924\u093f\u091a\u094d\u092f\u093e \u092e\u0927\u094d\u092f\u0947 \u091c\u094b\u0930\u093e\u0924 \u0938\u0941\u091f\u0932\u094b , \u0924\u0940 \u0905\u091c\u0942\u0928\u0939\u0940 \u092e\u0932\u093e \u092e\u093e\u0930\u0924 \u0930\u093e\u0939\u093f\u0932\u0940, \u0924\u0940 \u092a\u0923 \u0938\u0941\u091f\u0932\u0940 , \u0924\u093f\u091a\u0940 \u092a\u0941\u091a\u094d\u091a\u0940 \u0924\u094d\u092f\u093e \u0928\u0902\u0924\u0930 \u0906\u0915\u0941\u0902\u091a\u0928 \u0915\u0930\u0924 \u0939\u094b\u0924\u0940.\n\n\u0924\u093f\u0928\u0947 \u092e\u093e\u091d\u093e \u0921\u093f\u0915 \u0924\u093f\u091a\u094d\u092f\u093e \u0924\u094b\u0902\u0921\u093e\u0924 \u0918\u0947\u0924\u0932\u093e, \u0938\u094d\u0935\u091a\u094d\u091b \u0915\u0947\u0932\u093e, \u092e\u0917 \u0924\u093f\u0928\u0947 \u092e\u093e\u091d\u0940 \u092a\u0901\u091f \u0938\u0930\u0915\u0935\u0932\u0940 \u0906\u0923\u093f \u092e\u0932\u093e \u092c\u094d\u0932\u0945\u0902\u0915\u0947\u091f\u092e\u0927\u094d\u092f\u0947  \u091d\u093e\u0915\u0932\u0947.\n\n\u0906\u092e\u094d\u0939\u0940 \u0928\u0947\u0939\u092e\u0940\u092a\u094d\u0930\u092e\u093e\u0923\u0947 \u0938\u0915\u093e\u0933\u0940 \u0909\u0920\u0932\u094b \u0906\u0923\u093f \u0926\u0941\u0938\u0931\u094d\u092f\u093e \u0926\u093f\u0935\u0936\u0940 \u0906\u0923\u0916\u0940 \u0915\u093e\u0939\u0940 \u092e\u0928\u094b\u0930\u0902\u091c\u0928\u093e\u0938\u093e\u0920\u0940 \u0917\u0947\u0932\u094b. \u092e\u0940 \u0915\u093e\u092f \u091d\u093e\u0932\u0947 \u0939\u0947 \u092e\u0932\u093e \u092e\u093e\u0939\u093f\u0924 \u0928\u093e\u0939\u0940 \u0905\u0938\u0947 \u092d\u093e\u0938\u0935\u0932\u0947.\n</div>\n'}}]);